(35, 1003, 26, ' \r\n#include <iostream>\r\n#include <cmath>\r\n#include <cstring>\r\n#include <cstdlib>\r\n#define INF 0x7FFFFFFF\r\nusing namespace std;\r\nstruct tt\r\n{\r\n	int x,y;\r\n	int lx,rx;\r\n}a[1001];\r\nint n,r,ans;\r\nint comp(const void *x,const void *y)\r\n{\r\n	return (*(tt *)x).lx>(*(tt *)y).lx?1:-1;\r\n}\r\nvoid execute()\r\n{\r\n	int i;\r\n	int m=-INF;\r\n	ans=0;\r\n	for(i=0;i<n;i++)\r\n	{\r\n		if(a[i].rx>m)\r\n		{\r\n			ans++;\r\n			m=a[i].lx;\r\n		}\r\n	}\r\n	cout<<ans<<endl;\r\n}\r\nint main()\r\n{\r\n	int T;\r\n	cin>>T;\r\n	int x,y;\r\n	while(T--)\r\n	{\r\n		cin>>n>>r;\r\n		for(int i=0;i<n;i++)\r\n		{\r\n			cin>>a[i].x>>a[i].y;\r\n			a[i].lx=a[i].x+sqrt(r*r-a[i].y*a[i].y);\r\n			a[i].rx=a[i].x-sqrt(r*r-a[i].y*a[i].y);\r\n		}\r\n		qsort(a,n,sizeof(a[0]),comp);\r\n		execute();\r\n	}\r\n	return 0;\r\n}        ', '1', 3, 0, 0, '2013-08-20 15:37:25'),
